async function run(input: any): Promise<any> {
  // Enhance data with additional processing
  const enhanced = {
    ...input,
    processedBy: 'TypeScript',
    processedAt: new Date().toISOString(),
    metadata: {
      totalValue: input.items?.reduce((sum: number, item: any) => sum + (item.price || 0), 0) || 0,
      categories: [...new Set(input.items?.map((item: any) => item.category) || [])],
      averagePrice: input.items?.length > 0 
        ? (input.items.reduce((sum: number, item: any) => sum + (item.price || 0), 0) / input.items.length).toFixed(2)
        : 0
    },
    summary: `Processing ${input.total_items} items for workflow ${input.workflow_id}`
  };

  return enhanced;
}

