interface Order {
  id: string;
  product: string;
  quantity: number;
  price: number;
  customer_id: string;
  timestamp: string;
}

interface Input {
  orders: Order[];
  total_orders: number;
  generated_at: string;
  generator: string;
}

interface Output {
  orders: Order[];
  analytics: {
    totalRevenue: number;
    averageOrderValue: number;
    topProduct: string;
    customerCount: number;
    summary: string;
  };
  processedBy: string;
  processedAt: string;
}

async function run(input: Input): Promise<Output> {
  const orders = input.orders || [];

  // Calculate analytics using TypeScript
  const totalRevenue = orders.reduce((sum, order) => sum + (order.price * order.quantity), 0);
  const averageOrderValue = orders.length > 0 ? totalRevenue / orders.length : 0;

  // Find most common product
  const productCounts = orders.reduce((counts, order) => {
    counts[order.product] = (counts[order.product] || 0) + order.quantity;
    return counts;
  }, {} as Record<string, number>);

  const topProduct = Object.entries(productCounts)
    .sort(([,a], [,b]) => b - a)[0]?.[0] || 'none';

  const uniqueCustomers = new Set(orders.map(o => o.customer_id)).size;

  return {
    orders,
    analytics: {
      totalRevenue: Math.round(totalRevenue * 100) / 100,
      averageOrderValue: Math.round(averageOrderValue * 100) / 100,
      topProduct,
      customerCount: uniqueCustomers,
      summary: `Processed ${orders.length} orders, $${totalRevenue.toFixed(2)} total revenue`
    },
    processedBy: 'TypeScript',
    processedAt: new Date().toISOString()
  };
}

