interface Input {
  report: string;
  analytics: any;
  orders: any[];
  languages_used: string[];
}

interface Output {
  finalReport: {
    textReport: string;
    htmlReport: string;
    metadata: {
      orderCount: number;
      totalRevenue: number;
      languagesUsed: string[];
      processingChain: string;
    };
  };
  success: boolean;
}

async function run(input: Input): Promise<Output> {
  const htmlReport = input.report
    .replace(/===/g, '')
    .replace(/\n/g, '<br>')
    .replace(/•/g, '&bull;')
    .replace(/SUMMARY:/g, '<h3>SUMMARY:</h3>')
    .replace(/TOP ORDERS:/g, '<h3>TOP ORDERS:</h3>');

  const metadata = {
    orderCount: input.orders?.length || 0,
    totalRevenue: input.analytics?.totalRevenue || 0,
    languagesUsed: input.languages_used || [],
    processingChain: 'Start → Python (Generate) → TypeScript (Analyze) → Python (Report) → TypeScript (Finalize)'
  };

  return {
    finalReport: {
      textReport: input.report,
      htmlReport: `<div style="font-family: monospace; padding: 20px; border: 1px solid #ccc;">${htmlReport}</div>`,
      metadata
    },
    success: true
  };
}
